class Solution:
    def longestPalindrome(self, s: str) -> str:
    
        global sol
        sol = ""

        def searchPalindrome(i, j):
            global sol
            while i >= 0 and j < len(s) and s[i] == s[j]:
                if (j - i + 1) > len(sol):
                    sol = s[i:j+1]
            
                i -= 1
                j += 1
        
        
        for i in range(len(s)):
            # odd lenght
            j, k = i, i
            searchPalindrome(j,k)
            
            # even length
            j, k = i, i+1
            searchPalindrome(j,k)
        
        return sol
    

